/* By default, the rollup field contains the rule type, or in the case of Banners, the rule type plus the location (e.g., top, middle, lower)
 * If different behavior is desired, an alternate field can be created for rules to roll up on, where rules can be combined or returned individually.
 * NOTE: If an alternate field is used for rollup, only rules containing that field will be returned at query time; so if this approach is used, 
 * ensure that the alternate rollup field is populated for all rules.  
 */

function (doc, ctx) {
  if (doc && doc.getId()) {
    var docID = doc.getId();
    var altRollupField = 'alt_rollup_s'
    var origRollup = doc.getFirstFieldValue("rollup");
    var ruleType = doc.getFirstFieldValue("type");
    var altRollup = "";

    if (origRollup && ruleType) {

      switch(ruleType) {
        case "boost_attributes":
          altRollup = docID;
          break;
        case "set_facets":
          altRollup = origRollup; //if multiple facet rules of different types should be returned, the differentiating value (eg, the group name) can be concatenated to the origRollup value: origRollup + Group
          break;
        //case "boost_list":
        //case "bury_list":
        //  altRollup = "boost_or_bury"; //use this approach to only fire the single highest priority rule among these ruleTypes
        //  break;
        default:
          altRollup = origRollup;
      }

    }
    doc.setField(altRollupField,altRollup)

    return doc;
  }
}

