(function () {
  "use strict";
  var LEN_LIMIT = 256
  var UtilLib
  var ArrayList = Java.type("java.util.ArrayList")

  var isDebug = false
  function logIfDebug(m) { if (isDebug && m) logger.info(m, Array.prototype.slice.call(arguments).slice(1)); }


