    for (var i = 0; i < sFields.length; i++) {
      var sField = sFields[i] //UnmodifiableEntry
      //logIfDebug("signals field {}",sField.getClass())
      var key = sField.getKey()
      var single = doc.getFirstFieldValue(key) //List<PipelineField>
      if (single.length > LEN_LIMIT) {
        var small = UtilLib.truncateString(single, LEN_LIMIT, true, true)
        doc.setField(key, small)
      }
      logIfDebug("signals field {} has type {} and size{}", key, single.getClass(), single.length)

    }
    //TODO: add pipeline code
