function getPrefix(fqs){
  var sep = "|"
  try {
    var depth = 0;
    if (null != fqs && fqs.length > 0) {
      for (var i = 0; i < fqs.length; i++) {
        var fq = fqs[i];
        logger.info("**FQ:"+fq);
        if (fq.startsWith(taxoFld)) {
          var fqVal = fq.split(':')[1].replace(/{.*}/,'');            //grab the value, but drop local params if they're there
          fqVal = fqVal.replace(/[(")]/g,"");                         //drop quotes and params 
          depth = fqVal.split(sep)[0];
          depth++;
          fqVal = fqVal.split(sep).slice(1,depth+1).join(sep);        //grab the prefix for the next level

          //logger.info("PREFIX:"+depth + sep + fqVal);
          return depth + sep + fqVal;
        }
      }
    }
    return 0;  //didn't find any category filters
  }
  catch (error) {
    logger.info("Error: "+error);
    return 0;
  }
}

