//drop all fq's with 'exact_' or 'desc_' prefix

function (request, response, ctx) {
  //with (DSL) {

  var fq_pattern = new RegExp('^exact_.*|^category_ss.*');
  var fqs = request.getParam('fq');
  logger.info('fqs:'+fqs);

  var newFqs = [];
  var needToSwap = false;

  for each (var fq in fqs){
    if (fq_pattern.test(fq)) {
      needToSwap = true;  
      logger.info("dropping rule param fq:"+fq)
    }
    else {
      newFqs.push(fq)
    }

  }

  if (needToSwap){
    request.removeParam('fq');
    for (i=0;i<newFqs.length;i++){
      request.addParam('fq',newFqs[i]);
      logger.info("adding fq:"+newFqs[i]);
    }
  }

  logger.info("NEW FQ:"+newFqs);

  //}
}

function cleanName(name) { 
  return name.replace(/{.*}/,''); //Drop local params, uppercase
}

function (request, response, ctx) {
  //with (DSL) {

  var allParams = request.getParams();
  var paramkeys = request.getParams().keySet();

  var collapseFilter = 'mainQuery';     //this is the tag referenced in the collapse parameter used to group products

  var productIdFld = 'productRollup_s';       //Field containing the parent id (usually the product ID; assign in index pipeline).
  var rollupBucket = "products";        //Arbitrary name for the rolled up counts
  var rollupParam = '"' + rollupBucket + '": "unique(' + productIdFld + ')"';   //JSON element to retrieve product-level rollup counts

  var countSortParam = '{"' + rollupBucket + '":desc}';                         //JSON element to specify Count sort (vs Index)
  var indexSortParam = '"index"';                                               //Index sort just needs to be wrapped in quotes

  var rules = ctx.getProperty("lw.rules");
  var stdFacetsToDrop = [];

  if (null != rules && rules.length > 0) {
    logger.info("***Rules Found***");

    for (var i=0; i < rules.length; i++) {
      var rule = rules[i];
      logger.info("***Rule #"+i);
      //logger.info(rule);
      //logger.info("RULE TYPE:"+rule["type"]);

      if (null != rule && rule["type"].equals("set_facets")) {
        logger.info("***Facet Rule***");
        var facets = rule.getFacetData();
        if (null != facets && facets.length > 0) {
          var HashMap = Java.type("java.util.HashMap");
          var multivalueMap = ctx.getProperty("multivalueMap.json.facet");
          if (multivalueMap == null) {
            multivalueMap = new HashMap;
          }
          var boostValuesMap = new HashMap;
          var buryValuesMap = new HashMap;
          var suppressValuesMap = new HashMap;

          for (var j = 0; j < facets.length; j++) {
            var facet = facets[j];

            //get details of facet from rule 
            var fieldName = facet.getFieldName();
            var minCount = facet.getMinCount();

            var sort = facet.getSort();
            var sortParam = (sort == "count") ? countSortParam : indexSortParam;

            var selectType = facet.getSelectType();
            //single select should only exclude the collapseFilter tag; multiselect facets must also exclude filters tagged with their own names
            var excludeTags = (selectType == 'multi') ? fieldName + ','+ collapseFilter : collapseFilter;


            logger.info("===============================");
            logger.info("facet: " + fieldName);
            logger.info("sort: " + sort);
            logger.info("minCount: " + minCount);
            logger.info("selectType: " + selectType);            
            logger.info("===============================");     

            var jf = '{' + rollupParam + ',   "' + fieldName + '": { "type": "terms",     "field": "' + fieldName + '", "sort": ' + sortParam + ', "limit":-1,  "mincount": ' + minCount +',  "facet": { ' + rollupParam + '} , "domain":{"excludeTags":"' + excludeTags + '"}  }}';
            request.addParam('json.facet', jf);
            logger.info(JSON.stringify(jf));

            var boostValues = facet.getBoostValues();
            if (null != boostValues && boostValues.length > 0) {
              boostValuesMap.put(fieldName, facet.getBoostValues());
            }
            var buryValues = facet.getBuryValues();
            if (null != buryValues && buryValues.length > 0) {
              buryValuesMap.put(fieldName, facet.getBuryValues());
            }
            var suppressValues = facet.getSuppressValues();
            if (null != suppressValues && suppressValues.length > 0) {
              suppressValuesMap.put(fieldName, facet.getSuppressValues());
            }

            //Add field name to list of facet params to drop
            stdFacetsToDrop.push(fieldName);

          }
          //TODO: This solution assumes a single active facet rule at a given time; if multiple facet rules fire, only the boosts/buries/suppresses for last facet rule will stored
          ctx.setProperty("multivalueMap.json.facet", multivalueMap);
          ctx.setProperty("boostValues.json.facet", boostValuesMap);
          ctx.setProperty("buryValues.json.facet", buryValuesMap);
          ctx.setProperty("suppressValues.json.facet", suppressValuesMap);

          //Drop traditional facets (they'll be added back after the query for QWB)
          var facetFields = request.getParam('facet.field');
          logger.info('stdFacetsToDrop:'+stdFacetsToDrop);
          logger.info("BEFORE:"+facetFields);
          var newFacetFields = [];

          for each (var ff in facetFields){
            ff = cleanName(ff);
            logger.info("ff:"+ff);
            if (stdFacetsToDrop.indexOf(ff) < 0) {
              newFacetFields.push(ff);
            }
            else {
              //find all the additional params for this facet and delete them
              var facetPattern = new RegExp('^f[.]'+ff+'.*');  
              for each(var param in paramkeys) {
                if (facetPattern.test(param)) {
                  request.removeParam(param);
                  break;
                }
              }
            }
          }

          logger.info("AFTER:"+newFacetFields);

          //Only delete traditional facets if we're not in QWB
          if(!request.hasParam('workbench')) {
            request.removeParam("facet.field");
            if (newFacetFields.length > 0) {
              request.putParams("facet.field",newFacetFields);
            }
          }

        }
      }
    }
  }
  //}
}

