 *     ],[
 *       "Green",4
 *     ],[
 *       "Black",4
 *     ],[
 *       "Tan",1
 *     ]
 *   ]
 * },
 */

function buildStdFacetsFromJSON(jsonFacets, facetFieldsObjects) {
  for (var jFacet in jsonFacets){
    var jfBuckets = jsonFacets[jFacet]["buckets"];

    if (!jfBuckets || !UtilLib.getTypeOf(jfBuckets).equals('ArrayList')) {
