    var newFacetVersion = new ArrayList();

    for (var j = 0; j < jfBuckets.length; j++) {
      var bucketValue = jfBuckets[j].get('val');    
      var bucketCount = jfBuckets[j].get(rollupBucket);

      var facEntry = new ArrayList();
      facEntry.add(bucketValue);

      if(bucketCount){
        facEntry.add(bucketCount);
      }
      else {
        facEntry.add(0);
      }

      newFacetVersion.add(facEntry);
      //logger.info("ADDING facEntry:"+facEntry);
