/*
 * Standard query stage entry point function
 */
function(request, response, ctx) {

  // Load in lucidworks.ps util library
  if (!UtilLib) {
    UtilLib = ctx.get("UtilLib");
  }

  var docs = response.getInnerResponse().getDocuments();
  if (!docs || docs.size() === 0) {
    return;
  }

  var inner = response.getInnerResponse();
  if (!UtilLib.getTypeOf(inner).equals('JSONResponse')) {
    return;
  }

  // Retrieve the data table from the JSONResponse
  var dict = inner.getUnderlyingObject();

  // Top level solr facet structure returned to the caller
  if (!dict.get('facet_counts')) {
    return;
  }

  // Facet fields are what we return to the caller and contains all of the facets and facet value counts
  var facetFieldsObjects = dict.get('facet_counts').get('facet_fields');
  if (!facetFieldsObjects) {
    return;
  }

  // JSON facets are the calculated rollupBucket (set by default to "products")  which combines values on productID but considers values on all SKUs
  var jsonFacets = dict.get('facets');
  if (!jsonFacets) {
    return;
  }

  buildStdFacetsFromJSON(jsonFacets, facetFieldsObjects);

}

