stages.script:
(function () {
    "use strict";
    var isDebug = false
    function logIfDebug(message){
      if(isDebug){ var args = Array.prototype.slice.call(arguments);
        if(args.length > 0){logger.info(args[0],args.slice(1));}}
    }

    /**
     * @see https://doc.lucidworks.com/fusion-server/5.3/concepts/querying/pipelines/custom-javascript-query-stages.html
     */
    return function(request,response,ctx){

      var abstractResponse = response.getInnerResponse(); // likely a JSONResponse but perhaps XMLResponse or other
      var underlyingResponse = abstractResponse.getUnderlyingObject();// if JSON then Map<String, Object>.  If XML then org.w3c.dom/Document

      //turn off spell check for subsiquent queries
      request.putSingleParam("spellcheck","false")


      // we could use nashorn's instanceof operator but future compatibility is unknown    
      var spellcheck, spellCorrection = null;
      if(abstractResponse && abstractResponse.getClass().getSimpleName().equals("JSONResponse")){

        spellcheck = underlyingResponse.get("spellcheck")  
              logIfDebug("\n\nspellcheck type is {}\n\n",   spellcheck.getClass())

        if (spellcheck) {
          var collationsVal = spellcheck.getOrDefault("collations",null);

          if (collationsVal) {
            var jsonnlVal = request.getFirstParam('json.nl');
            logIfDebug("extracting spellcheck collation for json.nl={}",jsonnlVal)
            if("map".equalsIgnoreCase(jsonnlVal)){
              spellCorrection = collationsVal.get("collation")
            }else if("arrmap".equalsIgnoreCase(jsonnlVal) && collationsVal.length > 0){
              spellCorrection = collationsVal[0].get("collation");
            } else if ("arrarr".equalsIgnoreCase(jsonnlVal) && collationsVal.length > 0) {
              spellCorrection = collationsVal[0][1];
            } else if (collationsVal.length > 1){  // jsonVal is likely flat, the default
              spellCorrection = collationsVal[1];
            }
              logIfDebug("found spellcheck collation {}",   spellCorrection)

          }
        }
      }
      else if(abstractResponse && abstractResponse.getClass().getSimpleName().equals("Document")){
        //TODO: extend for the org.w3c.dom.Document use case but short circuit for now
        return
      }
      else{
      return // short circuit
      }


      if (spellCorrection){
        var queryParam = request.hasParam('orig_q') ? 'orig_q' : 'q'
        request.putSingleParam(queryParam,spellCorrection);
        request.putSingleParam("_lwps_spell_corrected_q_s",spellCorrection)
        //The pipeline may want to change mm or otherwise widen the query if we don't have a colation so flag this fact
        ctx.put("hasSolrSpellCorrection",true)
        logIfDebug("Spellcheck Correction changing query to {}",spellCorrection)
      }    
    };
})();

function(request,response,ctx){

  if(response && response.initialEntity && typeof(response.initialEntity.appendObject) === 'function'){
            response.initialEntity.appendObject("ctx",ctx);
        }

}

function getPrefix(fqs){
  var sep = "|"
  try {
    var depth = 0;
    if (null != fqs && fqs.length > 0) {
      for (var i = 0; i < fqs.length; i++) {
        var fq = fqs[i];
        logger.info("**FQ:"+fq);
        if (fq.startsWith(taxoFld)) {
          var fqVal = fq.split(':')[1].replace(/{.*}/,'');            //grab the value, but drop local params if they're there
          fqVal = fqVal.replace(/[(")]/g,"");                         //drop quotes and params 
          depth = fqVal.split(sep)[0];
          depth++;
          fqVal = fqVal.split(sep).slice(1,depth+1).join(sep);        //grab the prefix for the next level

          //logger.info("PREFIX:"+depth + sep + fqVal);
          return depth + sep + fqVal;
        }
      }
    }
    return 0;  //didn't find any category filters
  }
  catch (error) {
    logger.info("Error: "+error);
    return 0;
  }
}

function (request, response, ctx) {
  taxoFld = "depth_catNamePath_ss";

  //assume facets set to true
  var fqs = request.getParam("fq");
  var currPrefix = getPrefix(fqs);

  request.addParam("facet.field",taxoFld);
  request.addParam("f."+taxoFld+".facet.prefix", currPrefix);

}

function (request, response, ctx) {
  //with (DSL) {
  var orig_taxo_field = "depth_catNamePath_ss";   //actual filter field received from the client; it contains depth prefixes (e.g., 2/Clothing/Dresses/Cocktail)

  //the following fields are used for DESCENDANT matching and EXACT matching, respectively; they do NOT contain depth prefixes, so those must be stripped from the fq's
  var desc_taxo_field = "catNamePath_ss";         
  var exact_taxo_field = "exact_catNamePath_ss";         
  var sep = "\\|";          //taxonomy node separator, usually pipe or slash; be sure to escape any regex special chars (e.g., pipe)      

  //find orig field in FQs
  var fqs = request.getParam('fq');
  logger.info('fqs:'+fqs);
  var orig_fq_pattern = new RegExp('.*'+orig_taxo_field+'.*');
  var field_replace_pattern = new RegExp(orig_taxo_field,'g');
  var value_replace_pattern = new RegExp('[0-9]'+sep,'g');

  for each (var fq in fqs){
    if (orig_fq_pattern.test(fq)) {
      var exact_fq = fq.replace(field_replace_pattern,exact_taxo_field);
      exact_fq = exact_fq.replace(value_replace_pattern,'');
      var desc_fq = fq.replace(field_replace_pattern,desc_taxo_field);
      desc_fq = desc_fq.replace(value_replace_pattern,'');
      request.addParam('fq',exact_fq)
      request.addParam('fq',desc_fq)
      ctx["exact_param_added"] = true;
      logger.info("DESC added fq:"+ desc_fq)
      logger.info("EXACT added fq:"+ exact_fq)
      break;  
    }
  }
  //}
}

//drop all fq's with 'exact_' or 'desc_' prefix

function (request, response, ctx) {
  //with (DSL) {

  var fq_pattern = new RegExp('^exact_.*|^catNamePath_ss.*');
  var fqs = request.getParam('fq');
  logger.info('fqs:'+fqs);

  var newFqs = [];
  var needToSwap = false;

  for each (var fq in fqs){
    if (fq_pattern.test(fq)) {
      needToSwap = true;  
      logger.info("dropping rule param fq:"+fq)
    }
    else {
      newFqs.push(fq)
    }

  }

  if (needToSwap){
    request.removeParam('fq');
    for (i=0;i<newFqs.length;i++){
      request.addParam('fq',newFqs[i]);
      logger.info("adding fq:"+newFqs[i]);
    }
  }

  logger.info("NEW FQ:"+newFqs);

  //}
}

