import com.lucidworks.spark.util.SolrSupport
import java.time.LocalDateTime
import java.time.format.DateTimeFormatter
import java.time.ZoneOffset
import org.apache.spark.sql.types.{
    StructType, StructField, StringType}
import org.apache.spark.sql.Row
import scala.util.control._


// main PBL transform
def transform(inputDF: Dataset[Row]) : Dataset[Row] = {
    println("---BEGIN TYPE-BASED DOCUMENT LOAD---")

    var outputDF = spark.emptyDataFrame

    var counter = 1
    var currentTypeField =  ""
    val loop = new Breaks;
    // Loop through spark settings for the Type fields which should be in spark.typeField_* format
    loop.breakable {
        do {
            var boolCheck = false
            try
            {
                currentTypeField = sc.getConf.get("spark.typeField_" + counter)
                counter += 1
            }
            catch
            { 
                case x: NoSuchElementException => 
                { 
                    println("End of type field arguments.")
                    boolCheck = true
                }
            }
            if(boolCheck){ loop.break }

            println("CURRENT TYPE: " + currentTypeField)
            //Type fields should be in format: {type name}, {value field}, {img url field (optional, if not used then null)}, {document url field (optional, if not used then null)}
            val typeFieldArray = currentTypeField.split(",").map(_.trim)

            //Field names wrapped in ` to escape characters like .
            var typeName = typeFieldArray(0)
            var valueField = "`" + typeFieldArray(1) + "`"
            var imgField = "`" + typeFieldArray(2) + "`"
            var docField = "`" + typeFieldArray(3) + "`"

            //Define columns
            var idCol = regexp_replace(concat(lit(typeName + "_"),col(valueField)),"\\s+","").as("id")
            var valueCol = col(valueField).as("value_s")
            var typeCol = lit(typeName).as("type")

            //Handle optional fields and build documents for the current type name
            var currentDF = spark.emptyDataFrame
            if(imgField.equalsIgnoreCase("`null`") && docField.equalsIgnoreCase("`null`"))
            {
                currentDF = inputDF.select(idCol, valueCol, typeCol).distinct()
            }

            else if(docField.equalsIgnoreCase("`null`"))
            {
                var imageCol = col(imgField).as("image_url")
                currentDF = inputDF.select(idCol, valueCol, typeCol, imageCol).distinct()
            }
            else if(imgField.equalsIgnoreCase("`null`"))
            {
                var docCol = col(docField).as("document_url")
                currentDF = inputDF.select(idCol, valueCol, typeCol, docCol).distinct()
            }
            else
            {
                var imageCol = col(imgField).as("image_url")
                var docCol = col(docField).as("document_url")
                currentDF = inputDF.select(idCol, valueCol, typeCol, imageCol, docCol).distinct()
            }

            //combine the current type dataframe with the final output dataframe
            val cols1 = outputDF.columns.toSet
            val cols2 = currentDF.columns.toSet
            val total = cols1 ++ cols2 // union

            def expr(myCols: Set[String], allCols: Set[String]) = {
                allCols.toList.map(x => x match {
                    case x if myCols.contains(x) => col(x)
                    case _ => lit(null).as(x)
                })
            }

            outputDF = outputDF.select(expr(cols1, total):_*).union(currentDF.select(expr(cols2, total):_*))
            println(outputDF.head())
        }
        while(currentTypeField != null)
    }

    println("---END TYPE-BASED DOCUMENT LOAD---")
    println(outputDF.count() + " DOCUMENTS WRITTEN")

    outputDF.withColumn("indexed_date", lit(LocalDateTime.now(ZoneOffset.UTC)+"Z"))
    .withColumn("ta_type", lit("entity"))
}

