import com.lucidworks.spark.util.SolrSupport
import com.lucidworks.dc.spark.util.DatasetLoader
import org.apache.solr.client.solrj.SolrQuery
import org.apache.solr.client.solrj.response.QueryResponse
import scala.collection.mutable.ListBuffer
import org.slf4j.LoggerFactory

val logger = LoggerFactory.getLogger("AutoDenyOrApproveRewriteDetections")

object AutoDenyOrApproveRewriteDetections {

  def main(args:Array[String]) : Unit = {
    val zkHost = System.getProperty("solr.zkhost")
    val qwsCollection = "DW_Large_query_rewrite_staging"
    val productsCollection = "DW_Large"

    denyApproveOrKeepSuggestions(zkHost, qwsCollection, productsCollection, "type:(spell OR phrase OR tail) AND (source:DW_Large_spell_correction OR DW_Large_phrase_extraction OR DW_Large_head_tail) AND deployed:false", true, true)
  }

  /**
    * Iterate through the query rewrites staging collection for rules matching the filter (should be undeployed) and
    * validate original term vs suggested term performance. Each query rewrite will either be denied if it 
    * doesn't meet the keep critera, approved if it leads to more results, or nothing will be done to the rule
    * so that the business users can evaluate manually.
    */
  def denyApproveOrKeepSuggestions(zkHost:String, qwsCollection:String, productsCollection: String, filter:String, performDeny:Boolean, performApprove:Boolean) = {
    val solrQuery = new SolrQuery("*:*")
    solrQuery.set("spellcheck", "false")
    solrQuery.addFilterQuery(filter)
    solrQuery.setFields("id surface_form output type")
    solrQuery.setRows(100000)

    val qr: QueryResponse = SolrSupport.getCachedCloudClient(zkHost).query(qwsCollection, solrQuery)
    val resultsDocs = qr.getResults().listIterator()
    val rewritesToDeny = new ListBuffer[String]()
    val rewritesToApprove = new ListBuffer[String]()
    if (resultsDocs != null) {
      while (resultsDocs.hasNext) {
        val doc = resultsDocs.next()
        val rewriteType = doc.getFirstValue("type").toString

        val resultsFromOrigTerm = getNumResultsFromMainCollection(zkHost, productsCollection, doc.getFirstValue("surface_form").toString)

        var suggestedTerm = ""
        var resultsFromSuggestedTerm = 0L
        if (rewriteType == "phrase") {
          suggestedTerm =  "\"" + doc.getFirstValue("surface_form").toString + "\""
          resultsFromSuggestedTerm = getNumResultsFromMainCollection(zkHost, productsCollection, suggestedTerm)
        } else {
          suggestedTerm = doc.getFirstValue("output").toString
          resultsFromSuggestedTerm = getNumResultsFromMainCollection(zkHost, productsCollection, suggestedTerm)
        }

        // LESS TO MORE SCENARIOS
        // If we go from zero to greater than zero results - keep this suggestion
        // If we go from less to more at a significant percentage difference - keep this suggestion
        // Consider - less to more but under a threshold (ie from 0-2) may not be worthy
        // MORE TO LESS SCENARIOS
        // If we go from any number to zero - deny or remove this suggestion
        // If we go from more to less at a significant percentage difference - flag this suggestion
        // SAME RECALL SCENARIOS
        // If we go from less to more/more to less at an insignificant percentage difference - flag this suggestion

        val action = actionToTake(resultsFromOrigTerm, resultsFromSuggestedTerm)
        if (action == "deny") {
          rewritesToDeny += doc.getFirstValue("id").toString
        } else if (action == "keep") {
          rewritesToApprove += doc.getFirstValue("id").toString
        }
        println(action.toUpperCase + "::" + rewriteType + "::" + doc.getFirstValue("id") + "::" + doc.getFirstValue("surface_form") + "::" + resultsFromOrigTerm + "::" + suggestedTerm + "::" + resultsFromSuggestedTerm)
      }

      if (peformDeny) {
        for (id <- rewritesToDeny) {
// Uncomment this to perform an actual delete         SolrSupport.getCachedCloudClient(zkHost).deleteByQuery(qwsCollection, query, 1000).getStatus
          updateRewriteStatus(zkHost, qwsCollection, id, "denied")
        }
      }

      if (performApprove) {
        for (id <- rewritesToApprove) {
          updateRewriteStatus(zkHost, qwsCollection, id, "approved")
        }
      }
    }
  }

  /**
    * Create a dataframe in order to update a rewrite to either denied or approved.
    */
  def updateRewriteStatus(zkHost:String, qwsCollection:String, id:String, newStatus:String) = {
    val updateQuery = "id:" + id
    val opts = Map("zkHost" -> zkHost, "flatten_multivalued" -> "false", "fields" -> "id, deployed", "collection" -> qwsCollection, "query" -> updateQuery, "maxRows" -> "1")
    var rewriteDF = spark.read.format("solr").options(opts).load
    rewriteDF = rewriteDF.withColumn("review", lit(newStatus))
    //rewriteDF.show()
    val atomicAdds = DatasetLoader.sendAtomicAddsToSolr(rewriteDF, qwsCollection, "id", zkHost)        
  }

  /**
    * Implements the "action to take" logic based in results before/after
    */
  def actionToTake(numBefore: Long, numAfter: Long) = if (numBefore == 0 && numAfter > 0) "keep" else if (numBefore >= 0 && numAfter > numBefore) "keep" else if (numAfter == 0) "deny" else if (numBefore == numAfter) "deny" else "flag"  

  /**
    * Duplicates the customer specific filtering logic to run a query as from customer application to determine number of results.
    * Returns the number of results from for the query terms in the product collection.  
    * CUSTOMIZATION TODO: Replace the filters with the implementation specific filters applied by the client/pipeline
    */
  def getNumResultsFromMainCollection(zkHost:String, productsCollection:String, queryTerms:String): Long = {
    val solrQuery = new SolrQuery("spells:" + queryTerms)
    solrQuery.addFilterQuery("field1:foo")
    solrQuery.addFilterQuery("field2:bar")
    solrQuery.setFields("id")
    solrQuery.setRows(0)
    val qr: QueryResponse = SolrSupport.getCachedCloudClient(zkHost).query(productsCollection, solrQuery)
    qr.getResults().getNumFound()
  }
}

